INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportEmptyUnitHeader', N'', N'
توضیحات:
       در فرآیند محاسبه قبوض آب‌بها، تعداد واحدهای خالی از سکنه از محاسبات مربوط به مصرف آب کسر می‌گردد تا مبنای صدور قبض بر اساس میزان واقعی مصرف واحدهای فعال تعیین شود.
کاربرد:
      اجرای این رویکرد موجب می‌گردد محاسبات مربوط به قبوض مشترکین با دقت و عدالت بیشتری انجام پذیرد، چراکه هزینه‌های مصرف صرفاً بر اساس واحدهایی که واقعاً مورد استفاده قرار می‌گیرند محاسبه خواهد شد. این امر با بهینه‌سازی فرآیند صدور قبوض و حذف هزینه‌های غیرواقعی، زمینه افزایش وصول مطالبات و در نتیجه رشد درآمد شرکت را نیز فراهم می‌سازد.
', CAST(N'2025-11-09T11:26:42.6900000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
