INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES (N'searchASubscriberProHeader', N'', N'توضیحات: 
      در این بخش، امکان استفاده از ترکیب چندین فیلد و معیار جستجو به منظور دستیابی به نتایج دقیق‌تر و منطبق‌تر بر نیاز کاربر فراهم شده است. این قابلیت به ویژه در شرایطی که نیاز به شناسایی مشترکین بر اساس مجموعه‌ای از ویژگی‌ها و مشخصات خاص وجود دارد، ابزاری کارآمد برای انجام جستجوهای هدفمند و جامع به شمار می‌رود.

کاربرد:
      استفاده از جستجوی دقیق زمانی که نیاز به فیلتر کردن و یافتن مشترک بر اساس ترکیبی از چندین مشخصه یا معیار خاص وجود دارد، به‌منظور شناسایی هدفمند و جامع‌تر مشترکین.

', CAST(N'2025-11-09T11:42:52.7100000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES (N'searchASubscriberSimpleHeader', N'', N'توضیحات: 
       در این بخش، کاربر می‌تواند با وارد کردن یک متن دلخواه، به جستجوی سریع اطلاعات مشترکین مورد نظر بپردازد. سیستم به‌صورت خودکار متن واردشده را با مقادیر اطلاعاتی موجود نظیر شماره اشتراک، شماره ردیف، شناسه قبض، نام، نام خانوادگی، آدرس، شماره تلفن همراه، قطر کنتور، تعداد آحاد تجاری، تعداد آحاد مسکونی یا سایر آحاد مقایسه کرده و در صورت یکسان بودن، رکورد مربوطه را شناسایی و نمایش می‌دهد.

کاربرد:
       استفاده از جستجوی سریع زمانی که تنها یک مشخصه از مشترک (مانند شماره اشتراک، نام یا شماره موبایل) در دسترس است و نیاز به یافتن اطلاعات کلی او وجود دارد.', CAST(N'2025-11-09T11:43:26.6733333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportWithoutBillDetailHeader', N'', N'توضیحات: 
مشترکینی که به دلایل مختلف، صدور قبض برای آن‌ها امکان‌پذیر نبوده است. این دلایل می‌تواند شامل عدم انجام عملیات قرائت در دوره مربوطه، نبود یا نقص اطلاعات ضروری از جمله تاریخ نصب انشعاب، عدم همکاری مشترک با مأمور قرائت در زمان مراجعه، یا سایر موانع و شرایطی باشد که منجر به عدم تکمیل فرآیند صدور قبض گردیده است.
کاربرد:
    شناسایی مشترکینی که به هر دلیل برای آن‌ها قبض صادر نشده که این امر باعث افزایش درآمد شرکت خواهد شد. 
', CAST(N'2025-11-09T11:45:36.9300000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
