INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportMalfunctionDetailHeader', N'', N'توضیحات: 
       مشترکینی که کنتور آن‌ها در حال حاضر دچار نقص یا خرابی است و آخرین قرائت ثبت‌شده نیز مربوط به وضعیت خرابی کنتور بوده و از آن تاریخ تاکنون نسبت به تعویض کنتور اقدامی صورت نگرفته است.
کاربرد:
شناسایی مشترکینی که کنتور آن‌ها معیوب یا دچار نقص فنی است، به‌منزله امکان وجود هدررفت آب در شبکه مصرفی ایشان بوده و رفع این مشکل می‌تواند ضمن پیشگیری از اتلاف منابع آبی، زمینه ارائه خدمات دقیق‌تر، سریع‌تر و باکیفیت‌تر به مشترکین را فراهم آورد و در عین حال، موجب بهبود فرآیند وصول مطالبات و افزایش درآمد شرکت گردد.
', CAST(N'2025-11-09T11:47:51.0166667' AS DateTime2), N'برنامه نویس2', NULL, NULL)
