INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportHouseholdNumberDetailHeader', N'', N'توضیحات:
       شناسایی و ثبت خانوارهایی که به دلیل شرایط اقتصادی و مشکلات مالی، تعداد بیشتری از افراد در یک واحد مسکونی زندگی می‌کنند، به‌منظور بهره‌مندی موقت از تخفیفات تعیین‌شده توسط اداره تا تاریخ مشخص. 
کاربرد:
       این فرآیند موجب می‌شود شرکت آب و فاضلاب بتواند در بازه زمانی مقرر، خدمات بهتری به این دسته از خانوارها ارائه دهد و نیازهای آن‌ها را به شکل دقیق‌تری مدیریت نماید. پس از اتمام مهلت تعیین‌شده، تخفیف‌ها قطع شده و محاسبه هزینه‌ها طبق تعرفه عادی صورت خواهد گرفت. این رویکرد علاوه بر حمایت موقت از اقشار آسیب‌پذیر، شفافیت در ارائه خدمات و مدیریت صحیح منابع مالی شرکت را نیز تضمین می‌کند
', CAST(N'2025-11-09T11:49:31.4900000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportHouseholdNumberSummaryByZoneHeader', N'', N'توضیحات:
       شناسایی و ثبت خانوارهایی که به دلیل شرایط اقتصادی و مشکلات مالی، تعداد بیشتری از افراد در یک واحد مسکونی زندگی می‌کنند، به‌منظور بهره‌مندی موقت از تخفیفات تعیین‌شده توسط اداره تا تاریخ مشخص. 
کاربرد:
       این فرآیند موجب می‌شود شرکت آب و فاضلاب بتواند در بازه زمانی مقرر، خدمات بهتری به این دسته از خانوارها ارائه دهد و نیازهای آن‌ها را به شکل دقیق‌تری مدیریت نماید. پس از اتمام مهلت تعیین‌شده، تخفیف‌ها قطع شده و محاسبه هزینه‌ها طبق تعرفه عادی صورت خواهد گرفت. این رویکرد علاوه بر حمایت موقت از اقشار آسیب‌پذیر، شفافیت در ارائه خدمات و مدیریت صحیح منابع مالی شرکت را نیز تضمین می‌کند
', CAST(N'2025-11-09T11:50:22.9466667' AS DateTime2), N'برنامه نویس2', NULL, NULL)
