INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportUnPaidHeader', N'', N'توضیحات: 
       برای مشترک مورد نظر قبض جدید صادر گردیده است، اما تا این تاریخ هیچ‌گونه پرداختی بابت قبوض پیش‌تر صادر شده از سوی ایشان صورت نگرفته و بدهی‌های پیشین همچنان باقی مانده است. 
کاربرد:
    شناسایی مشترکینی که ممکن است با انگیزه خاصی هیچگونه پرداختی نداشته باشند که این امر موجب افزایش درآمد شرکت می‌شود. 
', CAST(N'2025-11-09T11:57:53.3000000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
