INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportUnConfirmedSubscribersHeader', N'', N'توضیحات: 
       مشترکین یا متقاضیانی که از مراحل دریافت خدمات،  پس از انجام فرآیند ارزیابی و محاسبه دقیق هزینه‌ها، مبلغ مربوطه به ایشان اعلام شده است، لیکن تاکنون نسبت به پرداخت هزینه اقدام نکرده و فرآیند مربوطه را به صورت نیمه‌کاره باقی گذاشته‌اند.
کاربرد:
     علاوه بر ارتقاء کیفیت خدمات‌رسانی، این امر موجب افزایش درآمد شرکت نیز می‌گردد.
      ممکن است برخی از مشترکینی که به ایشان مبالغ مربوطه اعلام شده و نسبت به پرداخت آن اقدام نکرده‌اند، به‌صورت غیرمجاز از انشعاب استفاده کرده باشند.
', CAST(N'2025-11-09T12:29:53.1433333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportNonPermanentBranchDetailHeader', N'', N'توضیحات: 
      بر اساس مفاد قرارداد، مشترک دارای انشعاب غیر دائم موظف است ظرف مدتی معین از تاریخ انعقاد قرارداد، نسبت به اخذ مجوزهای لازم از مراجع و ارگان‌های ذی‌ربط اقدام کند. در صورتی که تا پایان این مهلت قرارداد (برای مثال سه‌ساله) مجوز اخذ نگردد، شرکت مجاز است بدون نیاز به اخطار مجدد، نسبت به جمع‌آوری و برچیدن انشعاب اقدام نماید.
کاربرد:
      این گزارش به دو صورت خلاصه و جزئیات امکان شناسایی مشترکینی که دارای انشعاب غیر دائم هستند و امکان ابلاغ مهلت مشخص برای اخذ مجوز از مراجع ذی‌ربط را دارند می‌دهد.
', CAST(N'2025-11-09T12:30:56.3333333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
