INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportSWInstallationByZoneSummaryHeader', N'', N'توضیحات:
       این فرآیند با هدف یکپارچه‌سازی اطلاعات و تسهیل در پایش عملیات نصب انشعاب‌های آب و فاضلاب طراحی شده است. با ادغام گزارشات بر اساس تاریخ نصب و انتخاب بازه زمانی مورد نظر، امکان شناسایی و بررسی انشعاب‌هایی که به‌طور صحیح و در زمان مقرر نصب شده‌اند فراهم می‌گردد. گزارش‌های تولیدشده در این بخش، هم به شکل خلاصه برای ارائه تصویری کلی و سریع از وضعیت، و هم به شکل جزئی برای بررسی دقیق‌تر هر انشعاب در دسترس قرار دارند. علاوه بر این، مقایسه و تطبیق داده‌های به‌دست‌آمده با گزارشات معاونت بهره‌برداری، موجب شفافیت عملکرد، افزایش دقت در نظارت و بهبود کیفیت خدمات‌رسانی می‌شود. این رویکرد نه تنها به مدیریت بهینه منابع و جلوگیری از بروز خطا در ثبت اطلاعات کمک می‌کند، بلکه زمینه ارتقاء هماهنگی بین واحدهای اجرایی و بهره‌برداری را نیز فراهم می‌سازد. 
کاربرد:
       این بخش امکان ادغام دو گزارش مجزا بر اساس تاریخ نصب انشعاب آب و تاریخ نصب انشعاب فاضلاب را فراهم می‌سازد، به‌گونه‌ای که کاربر می‌تواند با یک کلیک، بازه زمانی مورد نظر را انتخاب و تعداد انشعاب‌های نصب‌شده در آن بازه را مشاهده نماید. علاوه بر این، گزارش‌ها هم به‌صورت خلاصه و هم به‌صورت جزئی در اختیار کاربر قرار می‌گیرد تا امکان بررسی سریع یا تحلیل دقیق اطلاعات فراهم شود. همچنین قابلیت بررسی صحت نصب انشعاب‌ها بر اساس بازه زمانی تعیین‌شده و مقایسه نتایج با گزارشات معاونت بهره‌برداری به منظور اطمینان از اجرای صحیح فرآیند نصب، در این بخش در دسترس قرار دارد.

گزارش های ادغام شده:
       خلاصه
•	بر اساس تاریخ نصب آب
•	بر اساس تاریخ نصب فاضلاب
       جزئیات
•	بر اساس تاریخ نصب آب
•	بر اساس تاریخ نصب فاضلاب
نکته: گزارش انشعاب نصب شده، امکان ترکیب چهار گزارش فوق را فراهم می‌نماید.
', CAST(N'2025-11-09T12:36:34.1800000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
