INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'servicelinkCalculationHeader', N'', N'
توضیحات: 
    گزارش «ریز محاسبه انشعاب» با هدف ارائه جزئیات کامل و دقیق اقلام محاسبه شده در قبوض انشعاب طراحی شده است. این گزارش برای شفاف‌سازی نحوه محاسبه اقلام انشعاب تهیه می‌شود.
 لازم به ذکر است که این گزارش در کنار گزارش «پیش‌پرداخت انشعاب» قرار دارد و هر دو به صورت مکمل یکدیگر، به بهبود فرآیند نظارت، کنترل و پاسخگویی به مشترکین کمک می‌کنند.
کاربرد:
    این گزارش امکان را می‌دهد جزئیات اقلام تشکیل‌دهنده قبض انشعاب را بررسی کرده و در صورت نیاز، خطاها یا ناهماهنگی‌های احتمالی را شناسایی کنند. همچنین، با ارائه این اطلاعات به مشترکین، شفافیت در فرآیند محاسبه قبض افزایش یافته و پاسخگویی به سوالات و درخواست‌های آنان بهبود می‌یابد.
', CAST(N'2025-11-09T12:47:27.3466667' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportPrePaymentHeader', N'', N'
توضیحات: 
    گزارش «پیش‌پرداخت انشعاب» با هدف ارائه اطلاعات دقیق و شفاف در خصوص مبالغ پیش‌پرداخت مربوط به انشعاب‌های آب و فاضلاب طراحی شده است. این گزارش علاوه بر نمایش مبلغ کلی پیش‌پرداخت، جزئیات مربوط به اقساط را نیز ارائه می‌دهد تا امکان بررسی کامل وضعیت مالی مشترک فراهم شود. اطلاعات نمایش‌داده‌شده در این گزارش، تصویر دقیقی از روند پرداخت‌ها و تعهدات مالی مشترک ارائه کرده و با گزارش «ریز محاسبه انشعاب» به‌صورت مکمل، امکان کنترل دقیق‌تر محاسبات و اطمینان از صحت مبالغ را فراهم می‌سازد.
کاربرد:
   این گزارش به کارشناسان و مدیران شرکت کمک می‌کند تا وضعیت مالی هر مشترک را از نظر پیش‌پرداخت و اقساط به‌طور کامل بررسی کنند. با مشاهده جزئیات نحوه تقسیط، امکان پیگیری آسان پرداخت‌ها و شناسایی سریع اقساط معوق یا مغایرت‌ها فراهم می‌شود. استفاده همزمان از این گزارش و گزارش «ریز محاسبه انشعاب» موجب افزایش دقت در کنترل فرآیندهای مالی، ارتقاء شفافیت و بهبود پاسخگویی به مشترکین خواهد شد.
', CAST(N'2025-11-09T12:48:30.9466667' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'waterCalculationDetailHeader', N'', N'توضیحات: 
    گزارش «ریز محاسبه آب‌بها» با هدف ارائه جزئیات دقیق و شفاف از نحوه محاسبه مبلغ آب‌بها طراحی شده است. این گزارش کلیه اقلام و مؤلفه‌های درج‌شده بر روی قبض آب، از جمله میزان مصرف، نرخ‌ها، مالیات‌ها، عوارض و سایر هزینه‌های مرتبط را به تفکیک نمایش می‌دهد. استفاده از این گزارش امکان شناسایی و رفع اشکالات احتمالی در فرآیند محاسبه را فراهم می‌کند.
کاربرد:
   این گزارش ابزاری کارآمد برای بررسی صحت محاسبات آب‌بها، شفاف‌سازی اقلام مندرج در قبض و عیب‌یابی موارد مشکوک به خطاست. استفاده از این گزارش، موجب افزایش دقت، ارتقاء کیفیت پاسخ‌گویی و بهبود فرآیندهای محاسبه آب‌بها خواهد شد.
', CAST(N'2025-11-09T12:49:11.4433333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportHandoverSummaryHeader', N'', N'توضیحات: 
      گزارش «نوع واگذاری» با هدف نمایش و تحلیل اطلاعات مربوط به وضعیت واگذاری انشعاب‌ها تهیه شده است. برخلاف برخی شرکت های آب و فاضلاب که از کاربری فروش و مصرف برای محاسبه استفاده می‌کنند، در استان اصفهان، نوع واگذاری نیز به‌عنوان یک معیار مهم در محاسبه آب‌بها لحاظ می‌گردد. به‌عنوان مثال، ملکی که کاربری آن «مسکونی» ثبت شده اما در حال ساخت‌وساز است، در بخش نوع واگذاری با عنوان «ساخت‌وساز» مشخص می‌شود. این تمایز اهمیت بالایی دارد، زیرا تعرفه محاسبه آب‌بها برای کاربری‌های مختلف با نوع واگذاری متفاوت است. گزارش مذکور به دو صورت خلاصه و جزئیات ارائه می‌شود تا هم امکان دریافت تصویر کلی و هم بررسی دقیق هر مورد فراهم باشد.
کاربرد:
       این گزارش ابزاری برای پایش و بررسی وضعیت انشعاب‌ها بر اساس نوع واگذاری آن‌هاست. با استفاده از این گزارش می‌توان مواردی را که نوع واگذاری آن‌ها نیاز به بازبینی دارد شناسایی کرد و در نتیجه، دقت محاسبه آب‌بها و صحت اعمال تعرفه‌ها را بهبود بخشید. همچنین این گزارش، بستری مناسب برای گزارش‌گیری‌های مدیریتی و برنامه‌ریزی‌های مرتبط با فروش و مصرف فراهم می‌آورد.
', CAST(N'2025-11-09T12:49:51.3033333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
