INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportHandoverChangeHistoryHeader', N'', N'توضیحات: 
      گزارش «نوع واگذاری» با هدف نمایش و تحلیل اطلاعات مربوط به وضعیت واگذاری انشعاب‌ها تهیه شده است. برخلاف برخی شرکت های آب و فاضلاب که از کاربری فروش و مصرف برای محاسبه استفاده می‌کنند، در استان اصفهان، نوع واگذاری نیز به‌عنوان یک معیار مهم در محاسبه آب‌بها لحاظ می‌گردد. به‌عنوان مثال، ملکی که کاربری آن «مسکونی» ثبت شده اما در حال ساخت‌وساز است، در بخش نوع واگذاری با عنوان «ساخت‌وساز» مشخص می‌شود. این تمایز اهمیت بالایی دارد، زیرا تعرفه محاسبه آب‌بها برای کاربری‌های مختلف با نوع واگذاری متفاوت است. گزارش مذکور به دو صورت خلاصه و جزئیات ارائه می‌شود تا هم امکان دریافت تصویر کلی و هم بررسی دقیق هر مورد فراهم باشد.
کاربرد:
       این گزارش ابزاری برای پایش و بررسی وضعیت انشعاب‌ها بر اساس نوع واگذاری آن‌هاست. با استفاده از این گزارش می‌توان مواردی را که نوع واگذاری آن‌ها نیاز به بازبینی دارد شناسایی کرد و در نتیجه، دقت محاسبه آب‌بها و صحت اعمال تعرفه‌ها را بهبود بخشید. همچنین این گزارش، بستری مناسب برای گزارش‌گیری‌های مدیریتی و برنامه‌ریزی‌های مرتبط با فروش و مصرف فراهم می‌آورد.
', CAST(N'2025-11-09T12:50:08.8933333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportRuinedMeterIncomeDetailHeader', N'', N'توضیحات:
       گزارش «درآمد کنتور خراب» با هدف شناسایی مشترکینی که کنتور آن‌ها در حال حاضر دچار نقص یا خرابی است و آخرین قرائت ثبت‌شده نیز در وضعیت خرابی بوده، طراحی شده است. از زمان ثبت خرابی تاکنون تعویض کنتور انجام نشده و این وضعیت می‌تواند علاوه بر ایجاد احتمال هدررفت آب، بر میزان درآمد حاصل از این کنتورها نیز تأثیر بگذارد. این گزارش با ارائه جزئیات درآمدی مرتبط با هر کنتور معیوب، امکان بررسی دقیق میزان وصولی و تحلیل اثر خرابی بر محاسبه و صدور قبوض را فراهم می‌سازد.
کاربرد:
      استفاده از گزارش «درآمد کنتور خراب» به مدیران و کارشناسان کمک می‌کند تا ضمن شناسایی کنتورهای معیوب، درآمد واقعی حاصل از آن‌ها را نیز ارزیابی کنند. این ارزیابی می‌تواند مبنایی برای تصمیم‌گیری در خصوص اولویت‌بندی تعویض کنتورها، بهبود فرآیند محاسبه قبوض، جلوگیری از اتلاف منابع آبی و افزایش دقت در وصول مطالبات باشد. همچنین این گزارش، تصویری شفاف از اثرات مالی خرابی کنتور ارائه می‌دهد که می‌تواند در بهینه‌سازی عملکرد و افزایش درآمد شرکت نقش مؤثری ایفا کند.
', CAST(N'2025-11-09T12:51:10.4866667' AS DateTime2), N'برنامه نویس2', NULL, NULL)
