INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportUnreadDetailHeader', N'', N'توضیحات:
    گزارش «بسته و مانع» به منظور شناسایی و ثبت مواردی تهیه شده است که در جریان قرائت کنتور، موانعی موجب عدم انجام صحیح عملیات قرائت می‌شوند. این موانع می‌تواند شامل بسته بودن درب محل، ممانعت توسط مشترک، یا هرگونه شرایطی باشد که مانع دسترسی مأمور قرائت به کنتور می‌گردد. بر اساس بخشنامه، در صورتی که پس از تعداد دفعات مشخصی قرائت کنتور به دلیل این موانع امکان‌پذیر نباشد، وضعیت اشتراک به «حذف موقت» تغییر می‌یابد. در برخی موارد، این ممانعت ممکن است به صورت عمدی از سوی مشترک انجام شود، مانند باز نکردن درب یا ایجاد موانع دسترسی. این گزارش امکان ثبت دقیق جزئیات هر مورد و تحلیل روند بروز این وضعیت را فراهم می‌سازد.
کاربرد:
    این گزارش ابزاری برای نظارت و مدیریت مشترکینی است که به دلایل مختلف مانع از قرائت کنتور می‌شوند. با استفاده از این گزارش، می‌توان الگوهای تکرار این مشکل را شناسایی و اقدامات اصلاحی لازم را برنامه‌ریزی کرد. همچنین، این گزارش می‌تواند مبنایی برای هماهنگی با واحدهای حقوقی، پیگیری‌های لازم، و یا اطلاع‌رسانی به مشترکین در خصوص عواقب عدم همکاری در فرآیند قرائت باشد. تحلیل این داده‌ها در نهایت به بهبود دقت در ثبت مصرف، کاهش خطا در صدور قبوض و ارتقاء کیفیت خدمات‌رسانی کمک خواهد کرد.
', CAST(N'2025-11-09T12:55:34.8200000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
