INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportSWDistanceRegisterInstallationHeader', N'', N'توضیحات: 
      گزارش «واگذار تا نصب» با هدف پایش و کنترل مدت‌زمان بین تاریخ واگذاری انشعاب آب یا فاضلاب تا تاریخ نصب آن تهیه شده است. این گزارش امکان بررسی عملکرد کارگزاران لوازم انشعاب و پیمانکاران نصب را فراهم می‌سازد و نشان می‌دهد که فرآیند تحویل و نصب انشعاب در چه بازه زمانی انجام شده است. گزارش مذکور علاوه بر ثبت و نمایش جزئیات برای هر اشتراک، قابلیت تهیه گزارش‌های خلاصه برای مقایسه بین مناطق مختلف را نیز دارد.
کاربرد:
      این گزارش ابزاری برای مدیریت و ارزیابی فرآیند واگذاری تا نصب انشعاب‌ها است. با استفاده از آن می‌توان میزان سرعت و کیفیت عملکرد پیمانکاران و کارگزاران را سنجید، مناطق مختلف را با یکدیگر مقایسه نمود و در نتیجه با شناسایی نقاط ضعف، به بهبود فرآیندها کمک کرد. همچنین تسریع در روند نصب موجب افزایش رضایت مشترکین و صدور به‌موقع قبض مصرف می‌گردد که در نهایت به ارتقاء کیفیت خدمات‌رسانی و افزایش بهره‌وری منجر خواهد شد.
', CAST(N'2025-11-09T13:02:18.2133333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
