INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportUseStateReportDetailHeader', N'', N'توضیحات:
      گزارش «وضعیت انشعاب» به منظور ثبت، پایش و تحلیل شرایط مختلف انشعاب‌های برقرار و متصل به شبکه طراحی شده است. در این گزارش، مواردی همچون انشعاب‌هایی که پس از چند بار قرائت با وضعیت «بسته و مانع» مواجه شده و به «جمع‌آوری موقت» تغییر یافته‌اند، یا انشعاب‌هایی که به دلایلی نظیر تعمیرات داخلی، جابه‌جایی کنتور، تغییر وضعیت ملک و یا عدم حضور ساکنین امکان قرائت نداشته‌اند، ثبت و بررسی می‌گردد. این گزارش قابلیت ارائه اطلاعات به صورت چند انتخابی را فراهم کرده و با استفاده از فیلتر تاریخ و شماره اشتراک، امکان استخراج داده‌های دقیق‌تر و هدفمندتر را ایجاد می‌کند.
کاربرد:
      این گزارش به کارشناسان کمک می‌کند تا وضعیت واقعی انشعاب‌ها را در بازه‌های زمانی مشخص پایش کنند و انشعاب‌های دارای مشکل را به‌سرعت شناسایی نمایند. با استفاده از این داده‌ها می‌توان تصمیمات مدیریتی و عملیاتی دقیق‌تری در زمینه پیگیری وضعیت مشترکین، رسیدگی به موارد جمع‌آوری موقت، و برنامه‌ریزی جهت اصلاح یا سامان‌دهی انشعاب‌ها اتخاذ کرد. علاوه بر این، این گزارش موجب ارتقای دقت در فرآیند قرائت، افزایش شفافیت در وضعیت اشتراک‌ها، و بهبود کیفیت خدمات‌رسانی خواهد شد.
', CAST(N'2025-11-09T13:03:57.5500000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
