INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportContractualCapacityHeader', N'', N'توضیحات:
      گزارش «ظرفیت قراردادی» به منظور ثبت و بررسی ظرفیت مصرف آب تعیین‌شده برای املاک غیرمسکونی در زمان درخواست انشعاب تهیه شده است. بر اساس ضوابط، در هنگام واگذاری انشعاب به هر ملک غیرمسکونی، ظرفیتی مشخص متناسب با نوع کاربری، تعداد افراد و شرایط ملک تعیین می‌شود. مصرف آب مشترکین در چارچوب این ظرفیت محاسبه می‌گردد و در صورتی که میزان مصرف از ظرفیت قراردادی فراتر رود، مازاد آن با تعرفه متفاوت و متناسب با مقررات محاسبه خواهد شد. این گزارش امکان مشاهده و پایش ظرفیت‌های ثبت‌شده را در قالب جزئیات یا خلاصه و با فیلترهای متنوع از جمله تاریخ، شماره اشتراک و کاربری فراهم می‌کند.
کاربرد:
      این گزارش ابزاری کارآمد برای مدیریت و پایش مصرف مشترکین غیرمسکونی نسبت به ظرفیت تعیین‌شده در قرارداد است. با بهره‌گیری از آن می‌توان وضعیت مصرف را با ظرفیت‌های مصوب مقایسه کرد، مشترکینی که بیش از ظرفیت مجاز مصرف داشته‌اند را شناسایی نمود و اقدامات لازم برای اعمال تعرفه‌های مربوطه را انجام داد. علاوه بر این، گزارش ظرفیت قراردادی نقش مهمی در تحلیل روند مصرف، برنامه‌ریزی منابع آبی، کنترل درآمدها و ارائه گزارش‌های مدیریتی ایفا می‌کند.
', CAST(N'2025-11-09T13:05:20.3300000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportUsageDetailHeader', N'', N'توضیحات:
    گزارش «کاربری» با هدف ارائه اطلاعات دقیق و به‌روز از وضعیت انواع کاربری‌های تعریف‌شده در سامانه تهیه شده است. در زمان تدوین این مستند، ۴۴ نوع کاربری مختلف در سامانه موجود می‌باشد که هرکدام بر اساس نوع مصرف و شرایط ملک تعریف شده‌اند. این گزارش امکان انتخاب کاربری‌های موردنظر را فراهم می‌آورد و اطلاعات مرتبط با آن‌ها را در دو قالب «خلاصه» و «جزئیات» در اختیار کاربر قرار می‌دهد. همچنین، قابلیت فیلترگذاری بر اساس تاریخ، شماره اشتراک، ناحیه و نوع کاربری وجود دارد تا کاربران بتوانند به‌صورت دقیق‌تر داده‌های موردنیاز خود را استخراج و تحلیل کنند.
کاربرد:
   این گزارش ابزاری مناسب برای پایش و مدیریت کاربری‌های مختلف در سطح مناطق و مشترکین است. با استفاده از آن می‌توان دید جامعی از وضعیت کاربری‌ها به‌دست آورد، روند تغییرات را بررسی کرد و در صورت نیاز، تحلیل‌های دقیق‌تری در ارتباط با مصرف، برنامه‌ریزی منابع و کنترل تعرفه‌ها انجام داد. وجود دو سطح گزارش‌گیری (خلاصه و جزئیات) نیز امکان بهره‌برداری سریع و هم‌زمان امکان بررسی عمیق‌تر اطلاعات را برای کاربران فراهم می‌سازد.
', CAST(N'2025-11-09T13:07:54.9533333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
