INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportPendingPaymentHeader', N'', N'توضیحات: 
       گزارش «مشترکین بدهکار» به منظور شناسایی و پایش وضعیت بدهی مشترکین طراحی شده است. این گزارش امکان استخراج لیست مشترکین بدهکار را بر اساس معیارهایی همچون بازه تاریخی مشخص، مبلغ بدهی، تعداد دوره‌های بدون وصولی، مانده ابتدای و انتهای دوره و همچنین مبالغ پرداختی در طول این بازه فراهم می‌سازد. در بخش ورودی، کاربر می‌تواند چندین منطقه، کاربری فروش یا کاربری مصرف را به صورت انتخابی تعیین کند. این قابلیت‌ها کمک می‌کند تا اطلاعات دقیق‌تر و هدفمندتری از وضعیت بدهی مشترکین جمع‌آوری و تحلیل شود.
کاربرد:
    این گزارش ابزاری مناسب برای پایش و مدیریت کاربری‌های مختلف در سطح مناطق و مشترکین است. با استفاده از آن می‌توان دید جامعی از وضعیت کاربری‌ها به‌دست آورد، روند تغییرات را بررسی کرد و در صورت نیاز، تحلیل‌های دقیق‌تری در ارتباط با مصرف، برنامه‌ریزی منابع و کنترل تعرفه‌ها انجام داد. 
', CAST(N'2025-11-09T13:09:13.8333333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportInvalidPaymentHeader', N'', N'توضیحات: 
       گزارش «وصولی نامعتبر» به منظور شناسایی پرداخت‌هایی تهیه شده است که به دلایل مختلف با شناسه قبض یا شناسه پرداخت معتبر در سیستم مشترکین آبفا استان اصفهان مطابقت ندارند. این موارد می‌تواند شامل پرداخت‌هایی باشد که با استفاده از شناسه قبض‌های متعلق به سایر کدهای استان (مانند استان اصفهان) صورت گرفته‌اند، در حالی که چنین شناسه‌هایی در سیستم مشترکین آبفا استان اصفهان تعریف نشده است. همچنین ممکن است برخی پرداخت‌ها ناشی از وارد کردن اشتباه شناسه قبض یا شناسه پرداخت توسط مشترک باشند.
کاربرد:
       این گزارش ابزاری برای پایش و کنترل پرداخت‌های نامعتبر است و به واحدهای مالی کمک می‌کند تا مغایرت‌های احتمالی در فرآیند وصول را شناسایی و اصلاح کنند. از طریق این گزارش می‌توان پرداخت‌های اشتباه یا خارج از چارچوب را بررسی نموده، اقدامات اصلاحی لازم را انجام داد و از تکرار خطا در آینده پیشگیری کرد. در نهایت، استفاده از این گزارش باعث افزایش شفافیت مالی، دقت بیشتر در وصول مطالبات آب‌بها و ارتقای اعتماد مشترکین به سیستم خواهد شد.
', CAST(N'2025-11-09T13:15:36.2400000' AS DateTime2), N'برنامه نویس2', NULL, NULL)
