INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportMalfunctionToChangeDetailHeader', N'', N'توضیحات: 
       گزارش «کنتورهای خراب تعویض‌شده» با هدف ثبت و پایش فرآیند تعویض کنتورهایی طراحی شده است که به دلیل خرابی، نقص فنی یا مشکلات عملکردی از مدار خارج شده و کنتور جدید جایگزین آن‌ها شده است. این گزارش به دو صورت «خلاصه» و «جزئیات» قابل تهیه می‌باشد و با استفاده از بازه تاریخی، کلیه کنتورهای خراب تعویض‌شده را نمایش می‌دهد. علاوه بر نمایش موارد تعویض، فاصله زمانی تا انجام عملیات تعویض برای هر مورد ثبت شده و میانگین زمان تعویض در هر منطقه نیز محاسبه می‌گردد.
کاربرد:
      این گزارش ابزاری مهم برای پایش عملکرد مناطق و ارزیابی میزان کارآمدی پیمانکاران در فرآیند تعویض کنتورها است. از طریق آن می‌توان کمترین و بیشترین زمان صرف‌شده برای تعویض را شناسایی کرده و میانگین عملکرد هر منطقه را با مناطق دیگر مقایسه نمود. همچنین، این اطلاعات امکان شفاف‌سازی در گزارش‌گیری، بهبود کیفیت خدمات، و ارتقای سطح رضایت مشترکین را فراهم می‌آورد و مبنایی مناسب برای تصمیم‌گیری‌های مدیریتی و برنامه‌ریزی‌های آتی خواهد بود.
', CAST(N'2025-11-09T13:29:04.9933333' AS DateTime2), N'برنامه نویس2', NULL, NULL)
INSERT [UserGuidePool].[Faq] ( [Header], [Icon], [Content], [CreateDateTime], [CreatedBy], [DeleteDateTime], [DeletedBy]) VALUES ( N'reportMalfunctionToChangeSummaryHeader', N'', N'توضیحات: 
       گزارش «کنتورهای خراب تعویض‌شده» با هدف ثبت و پایش فرآیند تعویض کنتورهایی طراحی شده است که به دلیل خرابی، نقص فنی یا مشکلات عملکردی از مدار خارج شده و کنتور جدید جایگزین آن‌ها شده است. این گزارش به دو صورت «خلاصه» و «جزئیات» قابل تهیه می‌باشد و با استفاده از بازه تاریخی، کلیه کنتورهای خراب تعویض‌شده را نمایش می‌دهد. علاوه بر نمایش موارد تعویض، فاصله زمانی تا انجام عملیات تعویض برای هر مورد ثبت شده و میانگین زمان تعویض در هر منطقه نیز محاسبه می‌گردد.
کاربرد:
      این گزارش ابزاری مهم برای پایش عملکرد مناطق و ارزیابی میزان کارآمدی پیمانکاران در فرآیند تعویض کنتورها است. از طریق آن می‌توان کمترین و بیشترین زمان صرف‌شده برای تعویض را شناسایی کرده و میانگین عملکرد هر منطقه را با مناطق دیگر مقایسه نمود. همچنین، این اطلاعات امکان شفاف‌سازی در گزارش‌گیری، بهبود کیفیت خدمات، و ارتقای سطح رضایت مشترکین را فراهم می‌آورد و مبنایی مناسب برای تصمیم‌گیری‌های مدیریتی و برنامه‌ریزی‌های آتی خواهد بود.
